import React, { useState, useEffect } from "react";
import { SafeAreaView, View, Text, TouchableOpacity, StyleSheet } from "react-native";

function PreferencesScreen({ navigation }) {
  const [selectedHealthLabels, setSelectedHealthLabels] = useState([]);
  const [selectedDietLabels, setSelectedDietLabels] = useState([]);
  const [selectedCuisines, setSelectedCuisines] = useState([]);
  const [selectedDishTypes, setSelectedDishTypes] = useState([]);
  const [currentStep, setCurrentStep] = useState(1);

  const dietLabels = ["Balanced", "High-Fiber", "High-Protein", "Low-Carb", "Low-Fat", "Low-Sodium"];
  const healthLabels = ["Vegetarian", "Vegan", "Pescatarian", "Peanut-Free", "Dairy-Free", "Alcohol-Free", "Wheat-Free"];
  const cuisineType = ["American", "Asian", "British", "Caribbean", "Chinese", "Italian", "Mexican", "Japanese", "Indian"];
  const dishType = ["Biscuits and Cookies", "Bread", "Cereals", "Desserts", "Drinks", "Main Course"];

  const labelsByStep = {
    1: healthLabels,
    2: dietLabels,
    3: cuisineType,
    4: dishType,
  };

  const selectedLabelsByStep = {
    1: selectedHealthLabels,
    2: selectedDietLabels,
    3: selectedCuisines,
    4: selectedDishTypes,
  };

  const handleStepChange = (step) => {
    setCurrentStep(step);
  };

  const toggleSelection = (item, category) => {
    if (selectedLabelsByStep[currentStep].includes(item)) {
      setSelectedLabelsByStep[currentStep](
        selectedLabelsByStep[currentStep].filter((label) => label !== item)
      );
    } else {
      setSelectedLabelsByStep[currentStep]([...selectedLabelsByStep[currentStep], item]);
    }
  };

  useEffect(() => {
    console.log(`Step ${currentStep} Selections:`);
    console.log(selectedLabelsByStep[currentStep]);
  }, [currentStep, selectedLabelsByStep]);

  return (
    <SafeAreaView style={styles.container}>
      <Text style={styles.sectionHeader}>Step {currentStep}: Choose Labels</Text>
      {labelsByStep[currentStep].map((label, index) => (
        <TouchableOpacity
          key={index}
          style={[
            styles.option,
            selectedLabelsByStep[currentStep].includes(label) && styles.selectedOption
          ]}
          onPress={() => toggleSelection(label, currentStep)}
        >
          <Text style={styles.optionText}>{label}</Text>
        </TouchableOpacity>
      ))}

      {currentStep < 4 && (
        <TouchableOpacity
          style={styles.nextButton}
          onPress={() => handleStepChange(currentStep + 1)}
        >
          <Text style={styles.nextButtonText}>Next</Text>
        </TouchableOpacity>
      )}

      {currentStep === 4 && (
        <TouchableOpacity
          style={styles.finishButton}
          onPress={() => {
            console.log("Final Selections:");
            console.log(selectedLabelsByStep);
          }}
        >
          <Text style={styles.finishButtonText}>Finish</Text>
        </TouchableOpacity>
      )}
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 16,
    paddingTop: 16,
  },
  sectionHeader: {
    fontSize: 20,
    fontWeight: "bold",
  },
  option: {
    flexDirection: "row",
    alignItems: "center",
    padding: 10,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 7,
    marginVertical: 8,
  },
  selectedOption: {
    backgroundColor: "lightblue",
  },
  optionText: {
    fontSize: 16,
  },
  nextButton: {
    alignItems: "center",
    backgroundColor: "lightblue",
    padding: 12,
    borderRadius: 7,
  },
  nextButtonText: {
    fontSize: 18,
    fontWeight: "bold",
  },
  finishButton: {
    alignItems: "center",
    backgroundColor: "green",
    padding: 12,
    borderRadius: 7,
  },
  finishButtonText: {
    fontSize: 18,
    fontWeight: "bold",
    color: "white",
  },
});

export default PreferencesScreen;
