import { useState, useEffect } from "react";
import { SafeAreaView, View, Text, TouchableOpacity, StyleSheet, ScrollView } from "react-native";

function PreferencesScreen({ navigation }) {
  const [selectedHealthLabels, setSelectedHealthLabels] = useState([]);
  const [selectedDietLabels, setSelectedDietLabels] = useState([]);
  const [selectedCuisines, setSelectedCuisines] = useState([]);
  const [selectedDishTypes, setSelectedDishTypes] = useState([]);
  const [currentStep, setCurrentStep] = useState(1);

  const dietLabels = ["Balanced", "High-Fiber", "High-Protein", "Low-Carb", "Low-Fat", "Low-Sodium"];
  const healthLabels = ["Vegetarian", "Vegan", "Pescatarian", "Peanut-Free", "Dairy-Free", "Alcohol-Free", "Wheat-Free"];
  const cuisineType = ["American", "Asian", "British", "Caribbean", "Chinese", "Italian", "Mexican", "Japanese", "Indian"];
  const dishType = ["Biscuits and Cookies", "Bread", "Cereals", "Desserts", "Drinks", "Main Course"];

  const toggleSelection = (label, step) => {

    switch (step) {
      case 1:
        if (selectedDietLabels.includes(label)) {
            setSelectedDietLabels((prev) => prev.filter((item) => item !== label));
          } else {
            setSelectedDietLabels((prev) => [...prev, label]);
          }
        break;
      case 2:
        if (selectedHealthLabels.includes(label)) {
            setSelectedHealthLabels((prev) => prev.filter((item) => item !== label));
          } else {
            setSelectedHealthLabels((prev) => [...prev, label]);
          }
        break;
      case 3:
        if (selectedCuisines.includes(label)) {
            setSelectedCuisines((prev) => prev.filter((item) => item !== label));
          } else {
            setSelectedCuisines((prev) => [...prev, label]);
          }
        break;
      case 4:
        if (selectedDishTypes.includes(label)) {
            setSelectedDishTypes((prev) => prev.filter((item) => item !== label));
          } else {
            setSelectedDishTypes((prev) => [...prev, label]);
          }
        break;
      default:
        return;
    }
} 

const handleFinishSelections = () => {
    console.log("Final Selections:");
    console.log("Diet Labels:", selectedDietLabels);
    console.log("Health Labels:", selectedHealthLabels);
    console.log("Cuisines:", selectedCuisines);
    console.log("Dish Types:", selectedDishTypes);
};

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
          <Text style={styles.sectionHeader}>Step {currentStep} of 4</Text>
          {currentStep === 1 && (
            <>
                <Text>Select your diet type</Text>
                {dietLabels.map((label, index) => (
                <TouchableOpacity
                    key={index}
                    style={[
                    styles.option,
                    ]}
                    onPress={() => toggleSelection(label, currentStep)}
                >
                    <Text style={styles.optionText}>{label}</Text>
                </TouchableOpacity>
                ))}
            </>
          )}
          {currentStep === 2 && (
            <>
                <Text>Select your health labels</Text>
                {healthLabels.map((label, index) => (
                <TouchableOpacity
                    key={index}
                    style={[
                    styles.option,
                    ]}
                    onPress={() => toggleSelection(label, currentStep)}
                >
                    <Text style={styles.optionText}>{label}</Text>
                </TouchableOpacity>
                ))}
            </>
          )}
          {currentStep === 3 && (
            <>
                <Text>Select your preferred cuisines</Text>
                {cuisineType.map((label, index) => (
                <TouchableOpacity
                    key={index}
                    style={[
                    styles.option,
                    ]}
                    onPress={() => toggleSelection(label, currentStep)}
                >
                    <Text style={styles.optionText}>{label}</Text>
                </TouchableOpacity>
                ))}
            </>
          )}
          {currentStep === 4 && (
            <>
                <Text>Select your preferred dish type</Text>
                {dishType.map((label, index) => (
                <TouchableOpacity
                    key={index}
                    style={[
                    styles.option,
                    ]}
                    onPress={() => toggleSelection(label, currentStep)}
                >
                    <Text style={styles.optionText}>{label}</Text>
                </TouchableOpacity>
                ))}
            </>
          )}
          {currentStep < 4 && (
            <TouchableOpacity
              style={styles.nextButton}
              onPress={() => setCurrentStep(currentStep + 1)}
            >
              <Text style={styles.nextButtonText}>Next</Text>
            </TouchableOpacity>
          )}
          {currentStep === 4 && (
            <TouchableOpacity
              style={styles.finishButton}
              onPress={handleFinishSelections}
            >
              <Text style={styles.finishButtonText}>Finish</Text>
            </TouchableOpacity>
          )}
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#FFF'
  },
  container: {
    flex: 1,
    paddingHorizontal: 16,
    paddingTop: 16,
  },
  sectionHeader: {
    fontSize: 20,
    fontWeight: "bold",
  },
  option: {
    flexDirection: "row",
    alignItems: "center",
    padding: 10,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 7,
    marginVertical: 8,
  },
  selectedOption: {
    backgroundColor: "lightblue",
  },
  optionText: {
    fontSize: 16,
  },
  nextButton: {
    alignItems: "center",
    backgroundColor: "lightblue",
    padding: 12,
    borderRadius: 7,
  },
  nextButtonText: {
    fontSize: 18,
    fontWeight: "bold",
  },
  finishButton: {
    alignItems: "center",
    backgroundColor: "green",
    padding: 12,
    borderRadius: 7,
  },
  finishButtonText: {
    fontSize: 18,
    fontWeight: "bold",
    color: "white",
  },
});

export default PreferencesScreen;
