import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const RecipeList = ({ recipes }) => {
  return (
    <View style={styles.recipeListContainer}>
      {recipes.map((recipe, index) => (
        <View style={styles.recipeItem} key={index}>
            <Text>{recipe.recipe.label}</Text>
            <Text>{recipe.recipe.ingredients.length} Ingredients | {recipe.recipe.totalTime} min</Text>
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  recipeListContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    paddingHorizontal: 10,
    width: '100%',
  },
  recipeItem: {
    flexBasis: '45%',
    height: '149.25px',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'lightgray',
    margin: 10,
    borderRadius: 10,
  },
  recipeName: {
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default RecipeList;
