import React, { useEffect } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import AuthScreen from './screens/AuthScreen';
import HomeScreen from './screens/HomeScreen';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Stack = createStackNavigator();

function Navigation() {
  const [userInfo, setUserInfo] = React.useState(null);
  const [userIsAuthenticated, setUserIsAuthenticated] = React.useState(false);

  useEffect(() => {
    checkAuthenticationStatus();
  }, []);

  const checkAuthenticationStatus = async () => {
    try {
      const userIsAuthenticated = await AsyncStorage.getItem('@userIsAuthenticated');
      if (userIsAuthenticated === 'true') {
        setUserIsAuthenticated(true);
      }
    } catch (error) {
      console.error('Error checking authentication status:', error);
    }
  };

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName={userIsAuthenticated ? 'Home' : 'Auth'}>
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="Auth" component={() => <AuthScreen userInfo={userInfo} />} options={{ headerShown: false }}/>
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default Navigation;
