import React, { useState } from "react";
import { SafeAreaView, Text, View, TouchableOpacity, Image, StyleSheet } from "react-native";

function Cravings({ navigation }) {
  const [selectedMeal, setSelectedMeal] = useState(null);

  const mealTypes = [
    { label: "Breakfast", },
    { label: "Brunch",  },
    { label: "Lunch/Dinner",  },
    { label: "Snack", },
    { label: "Teatime",  },
  ];

  const handleMealSelection = (mealType) => {
    setSelectedMeal(mealType);
  };

  return (
    <SafeAreaView style={styles.container}>
      <Text style={styles.heading}>What type of meal are we having?</Text>
      {mealTypes.map((mealType, index) => (
        <TouchableOpacity
          key={index}
          style={[
            styles.mealButton,
            selectedMeal === mealType.label && styles.selectedMealButton
          ]}
          onPress={() => handleMealSelection(mealType.label)}
        >
          {/* <Image source={mealType.image} style={styles.buttonImage} /> */}
          <Text style={styles.buttonLabel}>{mealType.label}</Text>
        </TouchableOpacity>
      ))}
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  heading: {
    fontSize: 20,
    marginBottom: 20,
  },
  mealButton: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 10,
    padding: 10,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 7,
    height: 56,
    width: 305,
    paddingHorizontal: 10
  },
  selectedMealButton: {
    backgroundColor: "lightblue",
  },
  buttonImage: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
  buttonLabel: {
    fontSize: 16,
  },
});

export default Cravings;
