import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const RecipeList = ({ recipes }) => {
  return (
    <View style={styles.recipeListContainer}>
      {recipes.map((recipe, index) => (
        <View style={styles.recipeItem} key={index}>
          <View style={styles.recipeCuisine}>
            <Text>{recipe.recipe.cuisineType}</Text>
          </View>
          <View style={styles.recipeDetails}>
            <Text>{recipe.recipe.label}</Text>
            <Text>{recipe.recipe.ingredients.length} Ingredients | {recipe.recipe.totalTime} min</Text>
          </View>
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  recipeListContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    // paddingHorizontal: 10, 
    width: '100%',
  },
  recipeItem: {
    // flexBasis: '45%',
    height: '149.25px',
    width: '200px',
    justifyContent: 'center',
    backgroundColor: 'lightgray',
    margin: 10,
    borderRadius: 10,
    flexDirection: 'column', // Set the direction to row
  },
  recipeDetails: {
    alignItems: 'flex-start', // Adjust alignment as needed
    justifyContent: 'center',
    flex: 1, // Allow this section to take remaining space
    // paddingHorizontal: 10, 
  },
  recipeCuisine: {
    alignItems: 'flex-start', // Adjust alignment as needed
    justifyContent: 'center',
    flex: 1, // Allow this section to take remaining space
    paddingHorizontal: 10, // Add some padding for spacing
  },
});

export default RecipeList;
