import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';

const RecipeList = ({ recipes }) => {
  return (
    <View style={styles.recipeListContainer}>
      {recipes.map((recipe, index) => (
        <View style={styles.recipeItem} key={index}>
          <View style={styles.recipeCuisine}>
            <View style={styles.recipeCuisineLeft}>
              <Text style={styles.recipeCuisineName}>{recipe.recipe.cuisineType}</Text>
            </View>
            <View style={styles.recipeCuisineRight}>
                <MaterialIcons name="more-horiz" size={16} color="red" />
            </View>
          </View>
          <View style={styles.recipeDetails}>
            <Text>{recipe.recipe.label}</Text>
            <Text>{recipe.recipe.ingredients.length} Ingredients | {recipe.recipe.totalTime} min</Text>
          </View>
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  recipeListContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    width: '100%',
  },
  recipeItem: {
    height: '149.25px',
    width: '200px',
    justifyContent: 'center',
    backgroundColor: 'lightgray',
    margin: 10,
    borderRadius: 10,
    flexDirection: 'column',
  },
  recipeDetails: {
    alignItems: 'flex-start', 
    justifyContent: 'flex-end',
    flex: 1, 
    paddingHorizontal: 10, 
    paddingBottom: 10,
  },
  recipeCuisine: {
    flex: 1, 
    paddingHorizontal: 10, 
    paddingTop: 10,
    flexDirection: 'row',
    justifyContent: 'space-between', 
  },
  recipeCuisineName: {
    color: '#FFF',
    fontFamily: 'Poppins',
    fontSize: 14,
    textTransform: 'capitalize'
  },
  recipeCuisineLeft: {
    backgroundColor: 'green'
  },
  recipeCuisineRight: {
    width: 22, 
    height: 22, 
    borderRadius: 20, 
    backgroundColor: '#FFF',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default RecipeList;
